//render Stars
function renderStars(rating) {
const fullStars = Math.floor(rating);
const hasHalfStar = rating % 1 >= 0.5;
let stars = '';

for (let i = 0; i < fullStars; i++) {
    stars += '<i class="fas fa-star"></i>';
}

if (hasHalfStar) {
    stars += '<i class="fas fa-star-half-alt"></i>';
}

const emptyStars = 5 - fullStars - (hasHalfStar ? 1 : 0);
for (let i = 0; i < emptyStars; i++) {
    stars += '<i class="far fa-star"></i>';
}

return stars;
}

// Initialize the page
document.addEventListener('DOMContentLoaded', function() {
  renderGalleryItems();
  renderServices();
  renderSareeCollections();
  
  // Initialize gallery navigation
  initGalleryNavigation();
  
  // Navigation active link
  const navLinks = document.querySelectorAll('nav a');
  navLinks.forEach(link => {
      link.addEventListener('click', function() {
          navLinks.forEach(l => l.classList.remove('active'));
          this.classList.add('active');
      });
  });
});

// Gallery Navigation Functions
function initGalleryNavigation() {
  const gallery = document.getElementById('imageGallery');
  const prevBtn = document.getElementById('prevBtn');
  const nextBtn = document.getElementById('nextBtn');
  
  const scrollAmount = () => {
      const item = gallery.querySelector('.gallery-item');
      return (item.offsetWidth + 16) * 3;
  };
            
  nextBtn.addEventListener('click', () => {
      gallery.scrollBy({
          left: scrollAmount(),
          behavior: 'smooth'
      });
  });
  
  prevBtn.addEventListener('click', () => {
      gallery.scrollBy({
          left: -scrollAmount(),
          behavior: 'smooth'
      });
  });
            
            gallery.addEventListener('scroll', () => {
                prevBtn.disabled = gallery.scrollLeft <= 10;
                nextBtn.disabled = gallery.scrollLeft >= gallery.scrollWidth - gallery.clientWidth - 10;
            });
            
            gallery.dispatchEvent(new Event('scroll'));
            
            // Touch support for mobile devices
            let isDown = false;
            let startX;
            let scrollLeft;
            
            gallery.addEventListener('mousedown', (e) => {
                isDown = true;
                startX = e.pageX - gallery.offsetLeft;
                scrollLeft = gallery.scrollLeft;
            });
            
            gallery.addEventListener('mouseleave', () => {
                isDown = false;
            });
            
            gallery.addEventListener('mouseup', () => {
                isDown = false;
            });
            
            gallery.addEventListener('mousemove', (e) => {
                if(!isDown) return;
                e.preventDefault();
                const x = e.pageX - gallery.offsetLeft;
                const walk = (x - startX) * 2;
                gallery.scrollLeft = scrollLeft - walk;
            });
        }

        // Video Modal Functions
        function openModal(videoUrl) {
            const modal = document.getElementById('videoModal');
            const videoFrame = document.getElementById('modalVideo');
            videoFrame.src = videoUrl;
            modal.style.display = 'flex';
            document.body.style.overflow = 'hidden';
        }
        
        function closeModal() {
            const modal = document.getElementById('videoModal');
            const videoFrame = document.getElementById('modalVideo');
            modal.style.display = 'none';
            videoFrame.src = '';
            document.body.style.overflow = 'auto';
        }
        
        window.onclick = function(event) {
            const modal = document.getElementById('videoModal');
            if (event.target == modal) {
                closeModal();
            }
        }



  document.querySelector(".explore-btn").addEventListener("click", function () {
    window.location.href = "./components/collections.html";
  });

